import React from 'react';


function ProgressBar({session,focusDuration,breakDuration,isTimerRunning, setAria}){

    return (
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow= {
                    100 -
                    (100 * session.timeRemaining) /
                      (session.label === 'Focusing'
                        ? focusDuration * 60
                        : breakDuration * 60)
                  }// TODO: Increase aria-valuenow as elapsed time increases
                style={{
                    width: `${
                      100 -
                      (100 * session.timeRemaining) /
                        (session.label === 'Focusing'
                          ? focusDuration * 60
                          : breakDuration * 60)
                    }%`,
                  }}// TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      );

}

export default ProgressBar;
